import React, { Component } from 'react';
import { db } from "../firebase/firebase"
import LandingTable from './LandingTable';

class LandingPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      dataFromDB: []
    }
  }
  UNSAFE_componentWillMount() {
    db.ref(`playlist`).on("value", data => {
      const array = [];
      data.forEach(element => {

        db.ref(`users/${element.val().UID}/playlistList/${element.val().playlistID}`).on("value", data => {
          array.push({
            playlistTitle: data.val().playlistTitle,
            playlistVideo: data.val().playlistVideo
          })
        })

        this.setState({
          dataFromDB: array
        });
        console.log(this.state.dataFromDB);
      })
    })
  }

  getData  = () => {
    if(this.state.dataFromDB){
      return this.state.dataFromDB.map((value,key)=> {
        return <LandingTable 
        key={key}
        playlistTitle={value.playlistTitle}
        />
      })
    }
  }

  render() {
    return (
      <div className="row">
        {this.getData()}
      </div>
    );
  }
}

export default LandingPage;
