import React, { Component } from 'react';
import SignOutButton from "./SignOut";

class UserInfoMenu extends Component {
    constructor(props) {
        super(props);
        this.state = {
            buttonState: true,
        }
    }
    editUserInfo = () => {
        if (this.state.buttonState) {
            return (<button data-toggle="dropdown" className="btn btn-warning editUserInfo-button" type="button" aria-haspopup="true" aria-expanded="false">Edit Info</button>)
        } else return <button data-toggle="dropdown" className="btn btn-success editUserInfo-button" type="button">Save </button>
    }

    editClick = () => {
        this.setState({
            buttonState: false
        });
    }
    saveClick = () => {
        this.setState({
            buttonState: true
        });
    }

    render() {
        return (
            // <div>
            //     <a className="navbar-item dropdown-toggle" data-toggle="dropdown" href="#" role="button" aria-haspopup="true" aria-expanded="false">Account</a>
            //     <div className="dropdown-menu" style={{ width: "350px", left: "-255px" }}>
            //         <div className="userInfo row">
            //             <div className="col-4">
            //                 <img src={this.props.userPhoto} alt="/" style={{ height: "100px", borderRadius: "50%" }}></img>
            //             </div>
            //             <div className="col-8">
            //                 <p>{this.props.userName}</p>
            //                 <p>{this.props.userEmail}</p>
            //                 {this.editUserInfo()}
            //             </div>
            //         </div>
            //         <div className="dropdown-divider"></div>
            //         <SignOutButton />
            //     </div>
            // </div>
            <div className="btn-group dropdown">
                <button className="navbar-item dropdown-toggle"
                    type="button"
                    id="dropdownMenuButton" data-toggle="dropdown">
                    Account</button>
                <div className="dropdown-menu" style={{ width: "350px", left: "-255px" }}>
                    <form className="px-4 py-3">
                        <div className="form-group">
                            <label for="exampleDropdownFormEmail1">Email address</label>
                            <input type="email" className="form-control"
                                id="exampleDropdownFormEmail1" placeholder="email@example.com"/></div>
                            <div className="form-group">
                                <label for="exampleDropdownFormPassword1">Password</label>
                                <input type="password" className="form-control"
                                    id="exampleDropdownFormPassword1" placeholder="Password"/></div>
                                <div className="form-check">
                                    <input type="checkbox" className="form-check-input" id="dropdownCheck"/>
                                        <label className="form-check-label" for="dropdownCheck">
                                            Remember me</label>
                                            </div>
                                    <button type="submit" className="btn btn-primary">Sign in</button>
                                    </form>
                                <div className="dropdown-divider"></div>
                                <a className="dropdown-item" href="#">New around here? Sign up</a>
                                <a className="dropdown-item" href="#">Forgot password?</a>
                            </div>
                        </div>
        );
    }
}

export default UserInfoMenu;
