import React, { Component } from 'react';
import MyPlaylistItem from './MyPlaylistItem';
import { db } from "../firebase/firebase"
import { Link } from "react-router-dom";


class MyPlaylistTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            videoData: [],
            nextVideo: "",
            videoID: ""
        }
    }

    toSlug(str) {
        // Chuyển hết sang chữ thường
        str = str.toLowerCase();

        // xóa dấu
        str = str.replace(/(à|á|ạ|ả|ã|â|ầ|ấ|ậ|ẩ|ẫ|ă|ằ|ắ|ặ|ẳ|ẵ)/g, 'a');
        str = str.replace(/(è|é|ẹ|ẻ|ẽ|ê|ề|ế|ệ|ể|ễ)/g, 'e');
        str = str.replace(/(ì|í|ị|ỉ|ĩ)/g, 'i');
        str = str.replace(/(ò|ó|ọ|ỏ|õ|ô|ồ|ố|ộ|ổ|ỗ|ơ|ờ|ớ|ợ|ở|ỡ)/g, 'o');
        str = str.replace(/(ù|ú|ụ|ủ|ũ|ư|ừ|ứ|ự|ử|ữ)/g, 'u');
        str = str.replace(/(ỳ|ý|ỵ|ỷ|ỹ)/g, 'y');
        str = str.replace(/(đ)/g, 'd');

        // Xóa ký tự đặc biệt
        str = str.replace(/([^0-9a-z-\s])/g, '');

        // Xóa khoảng trắng thay bằng ký tự -
        str = str.replace(/(\s+)/g, '-');

        // xóa phần dự - ở đầu
        str = str.replace(/^-+/g, '');

        // xóa phần dư - ở cuối
        str = str.replace(/-+$/g, '');

        // return
        return str;
    }

    deletePlaylist = (playlistIDofUser, accessModifier, idDB) => {
        this.props.deletePlaylist(playlistIDofUser, accessModifier, idDB)
    }

    componentDidMount() {
        const db1 = db.ref(`playlist/${this.props.accessModifier}/${this.props.idDB}/playlistVideo`);
        let array = []
        db1.on("value", data => {
            if (data.val()) {
                data.forEach(element => {
                    array.push({
                        key: element.key,
                        videoTitle: element.val().videoTitle,
                        videoThumbnail: element.val().videoThumbnail
                    })
                })
                this.setState({
                    videoData: [...array]
                });
            }
        })
    }

    getVideo = () => {
        if (this.state.videoData) {
            return this.state.videoData.map((value, key) => {
                return <MyPlaylistItem
                    key={key}
                    playlistID={this.props.idDB}
                    videoID={value.key}
                    videoTitle={value.videoTitle}
                    videoThumbnail={value.videoThumbnail}
                    videoID2={(videoID, nextVideo) => this.getVideoID(videoID, nextVideo)}
                />
            })
        }
    }

    getVideoID = (videoID, nextVideo) => {
        this.setState({
            videoID: videoID,
            nextVideo: nextVideo
        });
    }

    render() {
        return (
            <div className="col-6"  >
                <div className="playlist-table border">
                    <div className="row">
                        <div className="col-9">
                            <header style={{ color: "white", fontSize: "20px" }}>{this.props.playlistTitle}</header>
                            <p style={{ color: "white", fontSize: "20px" }}>{this.props.quantity} video </p>
                        </div>
                        <div className="col-3">
                            <div className="btn-group">
                                <Link to={"/editmyplaylist/" + this.toSlug(this.props.playlistTitle) + "." + this.props.playlistIDofUser + ".html"}>
                                    <button className="btn btn-warning">Edit</button>
                                </Link>
                                <button className="btn btn-danger" onClick={(playlistIDofUser, accessModifier, idDB) => this.deletePlaylist(this.props.playlistIDofUser, this.props.accessModifier, this.props.idDB)} >Del</button>
                            </div>
                        </div>
                    </div>

                    <ul className="list-group" style={{ height: "430px", overflowY: "auto", overflowX: "hidden" }}>
                        {this.getVideo()}
                    </ul>
                </div>

            </div>
        );
    }
}

export default MyPlaylistTable;
