import React, { Component } from 'react';

class SearchBar extends Component {
    constructor(props){
        super(props);
        this.state={
            idVideo : '',
        }}
    
    pushItem  = (item) => {
        this.props.getItem(item);
    }
        
    render() {
        return (
            <div>
                <div class="button-group col-4">
                    <input onChange={(event)=>{this.isChange(event)}} type="text" class="form-control"  aria-describedby="helpId" placeholder="" />
                    <button type="button" onClick={(item)=>this.pushItem(this.state.idVideo)} class="btn btn-primary">Tìm kiếm</button>
                </div>
            </div>
        );
    }
}

export default SearchBar;
