import React, { Component } from 'react';

class UserInfoMenu extends Component {
    constructor(props){
        super(props);
        this.state={

        }}
    editUserInfo  = () => {
      if(this.state.buttonState){
        return (<div className="btn btn-warning editUserInfo-button" type="button">Edit Info</div>)
      }else return <div className="btn btn-success editUserInfo-button" type="button">Save </div>
    }
  
    editClick  = () => {
      this.setState({
        buttonState: false
      });
    }
    saveClick  = () => {
      this.setState({
        buttonState: true
      });
    }

    render() {
        return (
            <div>
                <a className="navbar-item dropdown-toggle" data-toggle="dropdown" href="#" role="button" aria-haspopup="true" aria-expanded="false">Account</a>
          <div className="dropdown-menu" style={{ width: "350px", left: "-255px"}}>
            <div className="userInfo row">
              <div className="col-4">
                <img src={this.props.userPhoto} alt="/" style={{ height: "100px", borderRadius: "50%" }}></img>
              </div>
              <div className="col-8">
                <p>{this.props.userName}</p>
                <p>{this.props.userEmail}</p>
                {this.editUserInfo()}
              </div>
            </div>
            <div className="dropdown-divider"></div>
            <SignOutButton />
          </div>
            </div>
        );
    }
}

export default UserInfoMenu;
