    this.state={
      dataFromDB : []
    }}
  UNSAFE_componentWillMount() {
    db.ref('playlist').on("value", data => {
      const array =[]
      data.forEach(element =>
        array.push(element.val()
        )
      )
      this.setState({
        dataFromDB: array
