    if(dataFromDB){
      return this.state.dataFromDB.map((value, key) => {
        return <MyPlaylistTable
          key ={key}
        />
      } )
    }
  }
  render() {
    return (
      <div className="container-fluid">
        {this.getData()}
      </div>
    );
  }
}

export default LandingPage;
