import React, { Component } from 'react';
import EditMyPlaylistItem from './EditMyPlaylistItem';
import { db } from "../firebase/firebase"
import SearchBar from "./SearchBar";
import SearchResult from "./SearchResult";


const UID = localStorage.getItem("UID")
class EditMyPlaylist extends Component {
    constructor(props){
        super(props);
        this.state={
            dataFromDB : [],
            videoID: "",
            thumbnailVideo: '',
            titleVideo: '',
            item: {},
        }}

        UNSAFE_componentWillMount(){
            let array = [];
        const db1 = db.ref(`users/${UID}/playlistList/${this.props.match.params.id}/playlistVideo`);
        db1.on("value", data => {
            data.forEach(element=>{
                array.push({
                    key: element.val().key,
                    videoID: element.val().videoID,
                    videoTitle:element.val().videoTitle,
                    videoThumbnail: element.val().videoThumbnail
                })
            })

            this.setState({
                dataFromDB : array
            });
        })
        }
        getThumbnail = (id) => {
            let a = "https://i.ytimg.com/vi/";
            let b = "/hqdefault.jpg";
            this.setState({
                thumbnailVideo: a + id + b
            });
        }
    
        getTitle = (id) => {
            let a = 'https://www.googleapis.com/youtube/v3/videos?key=AIzaSyCAaefSzfa_SLpbO1Mt7hl8faCwfwBgk_E&fields=items(snippet(title))&part=snippet,statistics&id=' + id;
            this.getJSON(a, (err, data) => {
                if (err === null && data !== null) {
                    this.setState({
                        titleVideo: data.items[0].snippet.title
                    });
                }
                else return err;
            })
        }
    
        getJSON = function (url, callback) {
            var xhr = new XMLHttpRequest();
            xhr.open('GET', url, true);
            xhr.responseType = 'json';
            xhr.onload = function () {
                var status = xhr.status;
                if (status === 200) {
                    callback(null, xhr.response);
                } else {
                    callback(status, xhr.response);
                }
            };
            xhr.send();
        };
    
        getItem = (idVideo) => {
            this.setState({
                titleVideo: "",
                thumbnailVideo: ""
            });
            this.getThumbnail(idVideo);
            this.getTitle(idVideo);
            this.setState({
                videoID: idVideo
            });
        }
    getVideo  = () => {
        if(this.state.dataFromDB){
            return this.state.dataFromDB.map((value,key)=>{
                return <EditMyPlaylistItem
                    key = {key}
                    id = {value.key}
                    videoID = {value.videoID}
                    videoTitle = {value.videoTitle}
                    videoThumbnail = {value.videoThumbnail}
                />
            })
        }

    }

    addVideo  = (item) => {
        const db1 = db.ref(`users/${UID}/playlistList/${this.props.match.params.id}/playlistVideo`);
        item.videoID = this.state.videoID;
        item.videoThumbnail = this.state.videoThumbnail;
        item.videoTitle = this.state.videoTitle;
        if(this.state.videoID ){
            db1.push(item);
        }
    }

    render() {
        return (
            <div className="container-fluid row">
                <div className="col-5">Info PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo Playlist</div>
                <div className="col-7">
                <div className="header_input mt-2 mb-3" >
                            <SearchBar getItem={(item) => { this.getItem(item) }} />
                            <SearchResult thumbnailVideo={this.state.thumbnailVideo} titleVideo={this.state.titleVideo} />
                    
                </div>
                <button className="btn btn-info" onClick={(item) => {this.addVideo(this.state.item)}}>Add to Playlist</button>
                    {this.getVideo()}
                    
                </div>
            </div>
        );
    }
}

export default EditMyPlaylist;
