import React, { Component } from 'react';
import MyPlaylistTable from './MyPlaylistTable';
import SearchBar from "./SearchBar";
import SearchResult from "./SearchResult";

class MyPlaylist extends Component {
    constructor(props) {
        super(props);
        this.state = {
            playlistTitle: "",
            playlistDes: "",
            playlistAM: "",
            videoID: "",
            idVideo: '',
            thumbnailVideo: '',
            titleVideo: ''
        }
    }

    isChange(event) {
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]: value
        });
    }

    getThumbnail = (id) => {
        let a = "https://i.ytimg.com/vi/";
        let b = "/hqdefault.jpg";
        this.setState({
            thumbnailVideo: a + id + b
        });
    }

    getTitle = (id) => {
        let a = 'https://www.googleapis.com/youtube/v3/videos?key=AIzaSyCAaefSzfa_SLpbO1Mt7hl8faCwfwBgk_E&fields=items(snippet(title))&part=snippet,statistics&id=' + id;
        this.getJSON(a, (err, data)=> {
            if (err === null && data !== null) {
                // console.log(data.items[0].snippet.title);
                this.setState({
                    titleVideo: data.items[0].snippet.title
                });
            }
            else return err;
        })
    }

    getJSON = function (url, callback) {
        var xhr = new XMLHttpRequest();
        xhr.open('GET', url, true);
        xhr.responseType = 'json';
        xhr.onload = function () {
            var status = xhr.status;
            if (status === 200) {
                callback(null, xhr.response);
            } else {
                callback(status, xhr.response);
            }
        };
        xhr.send();
    };

    getItem = (idVideo) => {
        this.getThumbnail(idVideo);
        this.getTitle(idVideo);
        this.setState({

        });
    }

    render() {
        return (
            <div className="container-fluid">
                <div className="row">
                    <div className="col-8">
                        <div className="row">
                            <MyPlaylistTable></MyPlaylistTable>
                            <MyPlaylistTable></MyPlaylistTable>
                            <MyPlaylistTable></MyPlaylistTable>
                            <MyPlaylistTable></MyPlaylistTable>
                        </div>

                    </div>
                    <form className="col-4 border text-center ">
                        <h3>Tạo Playlist</h3>
                        <div className="form-group ">
                            <label for="">Tên Playlist</label>
                            <input type="text" name="playlistTitle" onChange={(event) => this.isChange(event)} className="form-control mb-4" placeholder="Tên Playlist ... " required />
                            <label for="">Mô tả Playlist</label>
                            <input type="text" name="playlistDes" onChange={(event) => this.isChange(event)} className="form-control mb-4" placeholder="Mô tả Playlist ... " required />
                            <div className="form-group">
                                <label for="">Phân quyền</label>
                                <select name="playlistAM" className="form-control mb-4" onChange={(event) => this.isChange(event)} defaultValue="Lựa chọn phân quyền">
                                    <option>Lựa chọn phân quyền</option>
                                    <option value={1}>Bí mật</option>
                                    <option value={2}>Công khai</option>
                                </select>
                            </div>
                            <label for="">Link video </label>
                            <SearchBar getItem={(item) => { this.getItem(item) }} />
                            <SearchResult thumbnailVideo={this.state.thumbnailVideo} titleVideo={this.state.titleVideo} />
                            <button type="submit reset" className="btn btn-primary btn-lg btn-block ">Thêm Playlist</button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}

export default MyPlaylist;
