import React, {Component} from "react";
import { Navbar, NavbarBrand, Nav, NavItem, NavLink } from "reactstrap";
import { Link } from "react-router-dom";
import { GoogleLoginButton } from "react-social-login-buttons";

import SignOutButton from "./SignOut";
import * as routes from "../constants/routes";

import AuthUserContext from "./AuthUserContext";
const byPropKey = (propertyName, value) => () => ({
  [propertyName]: value
});

const INITIAL_STATE = {
  email: "",
  password: "",
  error: null,
  showingAlert: false
};

googleLogin = () => {
  const { history } = this.props;
  auth
    .doGoogleSignIn()
    .then(authUser => {
      console.log("authUser", authUser);

      db.doCreateUser(
        authUser.user.uid,
        authUser.user.displayName,
        authUser.user.email
      )
        .then(() => {
          history.push(routes.HOME); //redirects to Home Page
        })
        .catch(error => {
          this.setState(byPropKey("error", error));
        });
    })
    .catch(error => {
      this.setState(byPropKey("error", error));
    });
};


timer = () => {
  this.setState({
    showingAlert: true
  });

  setTimeout(() => {
    this.setState({
      showingAlert: false
    });
  }, 4000);
};
class Navigation extends Component {
  state = { ...INITIAL_STATE };
 

  Navigation = () => (
    <AuthUserContext.Consumer>
      {authUser =>
        authUser ? this.NavigationAuth(authUser) : this.NavigationNonAuth()
      }
    </AuthUserContext.Consumer>
  );
    
   NavigationAuth = ({ userInfo }) => (
    <Navbar color="light" light expand="md">
      <NavbarBrand>
        <Link to={routes.LANDING}> Landing </Link>
      </NavbarBrand>
      <Nav className="ml-auto" navbar>
        <NavItem>
          <NavLink>
            <Link to={routes.HOME}>Home</Link>
          </NavLink>
        </NavItem>
        <NavItem>
          <SignOutButton />
        </NavItem>
      </Nav>
    </Navbar>
  );

   NavigationNonAuth = () => (
    <Navbar color="light" light expand="md">
      <NavbarBrand>
        <Link to={routes.LANDING}> Landing </Link>
      </NavbarBrand>
      <Nav className="ml-auto" navbar>
        {/* <NavItem>
          <NavLink>
            <Link to={routes.SIGN_IN}>Sign In</Link>
          </NavLink>
        </NavItem> */}
        <GoogleLoginButton onClick={() => this.googleLogin()} />
      </Nav>
    </Navbar>
  );
  render() {
    const { email, password, error, showingAlert } = this.state;

    const isInvalid = password === "" || email === "";
    {showingAlert && (
      <Alert color="danger" onLoad={this.timer}>
        {error.message}
      </Alert>
    )}
    return (
      <div>
        {
          this.Navigation()
        }
      </div>
    );
  }
}





  
  export default Navigation;
