import React, { Component } from "react";
import { BrowserRouter, Route } from "react-router-dom";
import { Container } from "reactstrap";

import * as routes from "../constants/routes";

//nav stuff
import Navigation from "./Navigation";
import LandingPage from "./Landing";
import HomePage from "./Home";

import withAuthentication from "./withAuthentication";



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      idVideo: '',
      titleVideo: '',
      thumbnailVideo: ''
    }
  }
  youtube_parser(url) {
    var regExp = /^.*((youtu.be\/)|(v\/)|(\/u\/\w\/)|(embed\/)|(watch\?))\??v?=?([^#&?]*).*/;
    var match = url.match(regExp);
    return (match && match[7].length == 11) ? match[7] : false;
  }

  isChange = (event) => {
    this.setState({
      idVideo: this.youtube_parser(event.target.value),
    });
    this.setState({
      thumbnailVideo: this.getThumbnail(this.state.idVideo),
      titleVideo: this.getTitle(this.state.idVideo)
    });
    console.log(this.state.titleVideo);

  }


  getThumbnail = (id) => {
    let a = "https://i.ytimg.com/vi/";
    let b = "/hqdefault.jpg";
    return a + id + b;
  }

  getJSON = function (url, callback) {
    var xhr = new XMLHttpRequest();
    xhr.open('GET', url, true);
    xhr.responseType = 'json';
    xhr.onload = function () {
      var status = xhr.status;
      if (status === 200) {
        callback(null, xhr.response);
      } else {
        callback(status, xhr.response);
      }
    };
    xhr.send();
  };

  getTitle(id) {
    let a =  'https://www.googleapis.com/youtube/v3/videos?key=AIzaSyCAaefSzfa_SLpbO1Mt7hl8faCwfwBgk_E&fields=items(snippet(title))&part=snippet,statistics&id=' + id;
    this.getJSON(a, function(err, data){
      if (err === null && data !== null) console.log(data/items);
      else return err;
    })
  };

  render() {
    return (
      <div className="col-4">
        <input onChange={(event) => this.isChange(event)} type="text" className="form-control" aria-describedby="helpId" placeholder="" />
        <button type="button" class="btn btn-primary">Tim kiem</button>
        <br /> <br />
        <div class="card text-left">
          <img class="card-img-top" src="holder.js/100px180/" alt="" />
          <div class="card-body">
            <h4 class="card-title">Video Title: {this.state.titleVideo}</h4>
            <img src={this.state.thumbnailVideo} alt="/" style={{ height: "135px" }} />
            {/* {console.log(this.getThumbnail(this.state.idVideo))} */}
          </div>
        </div>
      </div>

    );
  }
}





