  // }

  getJSON = function (url, callback) {
    var xhr = new XMLHttpRequest();
    xhr.open('GET', url, true);
    xhr.responseType = 'json';
    xhr.onload = function () {
      var status = xhr.status;
      if (status === 200) {
        callback(null, xhr.response);
      } else {
        callback(status, xhr.response);
      }
    };
    xhr.send();
  };

  getTitle(id){
    return 'https://www.googleapis.com/youtube/v3/videos?key=AIzaSyCAaefSzfa_SLpbO1Mt7hl8faCwfwBgk_E&fields=items(snippet(title))&part=snippet,statistics&id=' + id;
  }

  // fetch(url).then(res => res.json()).then((out) => {
  //   console.log('Checkout this JSON! ', out);
  // })
  // .catch(err => { throw err });

  render() {
//     this.getJSON(URL,
// function(err, data) {
//   if (err !== null) {
//     alert('Something went wrong: ' + err);
//   } else {
//     console.log(data.items[0].snippet.title);
//   }
// });
    return (
      <div className="col-4">
        <input onChange={(event) => this.isChange(event)} type="text" className="form-control" aria-describedby="helpId" placeholder="" />
        <button type="button" class="btn btn-primary">Tim kiem</button>
        <br /> <br />
        <div class="card text-left">
          <img class="card-img-top" src="holder.js/100px180/" alt="" />
          <div class="card-body">
    <h4 class="card-title">{() => {this.getJSON(this.getTitle(this.state.idVideo), function(err, data){
  if(err === null) {
    return data.items[0].snippet.title}
    else alert('Something went wrong: ' + err);
  })} }Ten Video</h4>
            <img src={this.getThumbnail(this.state.idVideo)} alt="/" style={{ height: "135px" }} />
            {/* {console.log(this.getThumbnail(this.state.idVideo))} */}
          </div>
        </div>
      </div>

    );
  }
}





