import React, { Component } from 'react';
import YouTube from 'react-youtube';
import {Link,Redirect } from "react-router-dom"

class VideoStream extends Component {
    constructor(props){
        super(props);
        this.state={
            videoID : "",
            abc :"from child"
        }}


    videoOnReady = (event) => {
        event.target.playVideo();
    }
    videoStateChange = (event) => {
        // if(event.data === 0)
        }
    
    func  = (abc) => {
        this.props.func(abc)
    }

    render() {
        const opts = {
            height: '390',
            width: '640',
            playerVars: {
                autoplay: 1,
            },
        };
        return (
            <div>
                <YouTube
                    videoId={this.props.videoID}
                    opts={opts}
                    onReady={this.videoStateChange}
                    onStateChange={this.videoStateChange}
                />
                <button onClick={(abc) => {this.func(this.state.abc)}}>click</button>
            </div>
        );
    }
}

export default VideoStream;
