import React, { Component } from 'react';
import { db } from "../firebase/firebase"
import PlaylistItem from './MyPlaylistItem';
import VideoStream from './VideoStream';

class VideoPlayer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dataFromDB: [],
            nextVideo: "",
            videoID: "",
            videoTitle: "",
        }
    }

    componentDidMount() {
        db.ref(`playlist/public/${this.props.match.params.playlistID}`).on("value", data => {
            let accessModifier = "";
            if (data.val()) {
                accessModifier = "public";
            } else accessModifier = "private";
            const db1 = db.ref(`playlist/${accessModifier}/${this.props.match.params.playlistID}/playlistVideo`)
            db1.on("value", data => {
                let array = [];
                data.forEach(element => {
                    array.push({
                        videoID: element.key,
                        videoTitle: element.val().videoTitle,
                        videoThumbnail: element.val().videoThumbnail,
                    })
                })
                this.setState({
                    dataFromDB: array
                });
            })
            const db2 = db.ref(`playlist/${accessModifier}/${this.props.match.params.playlistID}/playlistVideo/${this.props.match.params.videoID}`)
            db2.on("value", data2 => {
                this.setState({
                    videoID: this.props.match.params.videoID,
                    nextVideo: data2.val().nextVideo,
                    videoTitle: data2.val().videoTitle,
                })
            })

            const db3 = db.ref(`playlist/${accessModifier}/${this.props.match.params.playlistID}`);
            db3.on("value", data => this.setState({ playlistTitle: data.val().playlistTitle }))
    })

}

getVideo = () => {
    if (this.state.dataFromDB) {
        return this.state.dataFromDB.map((value, key) => {
            return <PlaylistItem

                key={key}
                videoID={value.videoID}
                videoThumbnail={value.videoThumbnail}
                videoTitle={value.videoTitle}
                playlistID={this.props.match.params.playlistID}
                videoID2={(videoID, videoTitle) => this.getVideoID(videoID, videoTitle)}
            />
        })
    }
}

getVideoID = (videoID, videoTitle) => {
    this.setState({
        videoID: videoID,
        videoTitle: videoTitle
    });
}

func = (params) => {
    this.setState({
        videoID: params
    });
}

render() {
    console.log(this.state.nextVideo);
    return (
        <div className="container">
            <div className="row">
                <div className="col-7">
                    <VideoStream videoID={this.props.match.params.videoID} nextVideo={this.state.nextVideo} func={(params) => this.func(params)} playlistID={this.props.match.params.playlistID} ></VideoStream>
                    <p className="videoplayer-video-title">{this.state.videoTitle}</p>
                </div>
                <div className="col-5">
                    <ul className="list-group" style={{ height: "430px", overflowY: "auto", overflowX: "hidden" }}>
                        <p className="videoplayer-video-title">{this.state.playlistTitle}</p>
                        {this.getVideo()}
                    </ul>
                </div>
            </div>
        </div>
    );
}
}

export default VideoPlayer;
