import React, { Component } from 'react';
import MyPlaylistItem from './MyPlaylistItem';
import { db } from "../firebase/firebase"


const UID = localStorage.getItem("UID");
class MyPlaylistTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            videoData : []
        }
    }

    deletePlaylist  () {
        let db1 = db.ref(`users /${UID}/playlistList`);
        db1.child(this.props.id).remove()
    }

    UNSAFE_componentWillMount(){
        const db1 = db.ref(`users/${UID}/playlistList/${this.props.id}/playlistVideo`);
        let array = []
        db1.on("value", data => {
            console.log(data.val());
            data.forEach(element => {
                array.push({
                    key : element.key,
                    videoID: element.val().videoID,
                    videoTitle: element.val().videoTitle,
                    videoThumbnail: element.val().videoThumbnail
                })
            })
            this.setState({
                videoData : array
            });
        })
    }

    getVideo  = () => {
        if(this.state.videoData){
            return this.state.videoData.map((value, key)=>{
                return <MyPlaylistItem
                    key = {key}
                    id = {value.key}
                    videoID = {value.videoID}
                    videoTitle = {value.videoTitle}
                    videoThumbnail = {value.videoThumbnail}
                />
            })
        }
    }

    render() {
        return (
            <div className="col-6 border"  >
                <div className="row">
                    <div className="col-9">
                        <header>{this.props.playlistTitle}</header>
                        <p>Quantity: 1/n </p>
                    </div>
                    <div className="col-3">
                        <div className="btn-group">
                            <a class="btn btn-warning" href="#" >Edit</a>
                            <button class="btn btn-danger" onClick={() => this.deletePlaylist()} >Del</button>
                        </div>
                    </div>
                </div>

                <ul className="list-group" style={{ height: "430px", overflowY: "auto", overflowX: "hidden" }}>
                    {this.getVideo()}
                </ul>




                <div>Đây là footer</div><br />
            </div>
        );
    }
}

export default MyPlaylistTable;
