import React, { Component } from 'react';
import { db } from "../firebase/firebase"

const UID = localStorage.getItem("UID")

class EditMyPlaylistItem extends Component {

    getVideoLink (videoID){
        return "https://www.youtube.com/watch?v=" + videoID
    }

    deleteVideo  = () => {
        // const db1 = db.ref(`users/${UID}/playlistList/${this.props.playlistID}/playlistVideo`);
        // db1.child(this.props.id).remove();
        console.log(this.props.id);
    }

    render() {
        return (
            <div>
                <li className="list-group-item" style={{display:"flex"}}>
                <div className="row">
                    <div className="col-4">
                        <a href={this.getVideoLink(this.props.videoID)}>
                            <img src={this.props.videoThumbnail} alt="" style={{height:"56px"}}/>
                        </a>
                        
                    </div>
                    <div className="col">
        <p style={{ height:"30px",textOverflow: "ellipsis",overflow:"hidden"}}>{this.props.videoTitle}</p>
                    </div>
                    <button class="btn btn-danger" onClick={ () => this.deleteVideo()} style={{marginRight:"5px"}} >Del</button>
                </div>
                </li>
                
            </div>
        );
    }
}

export default EditMyPlaylistItem;
