import React, { Component } from 'react';
import YouTube from 'react-youtube';
import { useHistory, Link, Redirect } from 'react-router-dom';

const history = useHistory();
class VideoStream extends Component {
    constructor(props) {
        super(props);
        this.state = {
            videoID: "",
            redirect: false,
        }
    }
    videoOnReady = (event) => {
        event.target.playVideo();
    }
    // videoStateChange = (event) => {
    //     if (event.data === 0) {
    //         this.func(this.props.nextVideo)
    //     }
    // }
    stateChange = (event) => {
        if (event.data === 0) {
            this.setState({redirect : true});
        }
    }
    // videoStateChange = (event) => {
    //     if (event.data === 0) {
    //         window.location.replace(`/videoplayer/${this.props.playlistID}.${this.props.nextVideo}`)
    //     }
    // }

    func  = (param) => {
        this.props.func(param)
    }

    render() {
        const opts = {
            height: '390',
            width: '640',
            playerVars: {
                autoplay: 1,
            },
        };
        return this.state.redirect ? (<Redirect to ={"/videoplayer/" +this.props.playlistID +"."+this.props.nextVideo}/>) : (
            <div>
                <YouTube
                    videoId={this.props.videoID}
                    opts={opts}
                    onReady={this.videoStateChange}
                    onStateChange={this.videoStateChange}
                />
                
            </div>
        );
    }
}

export default VideoStream;
