import React, { Component } from 'react';
import {Link} from "react-router-dom";
import * as routes from "../constants/routes";

class PlaylistItem extends Component {
    constructor(props){
        super(props);
        this.state={
            videoInfo : {}
        }}


    getVideoLink = () => {
        return "https://www.youtube.com/watch?v=" + this.props.videoID
    }

    sendVideoInfo  = (videoID, nextVideo) => {
        this.props.videoID2 (videoID, nextVideo)
    }

    render() {
        return (
            <div>
                <li className="list-group-item">
                    <div className="row">
                        <div className="col-4">
                            <div>
                                <Link to={routes.VIDEOPLAYER + this.props.playlistID + "." + this.props.videoID}>
                                <img onClick={(videoID, videoInfo) => this.sendVideoInfo(this.props.videoID, this.props.nextVideo)} src={this.props.videoThumbnail} alt="" style={{ height: "56px" }} />
                                </Link>
                            </div>
                        </div>
                        <div className="col-8">
                            <p style={{ width: "200px", height: "30px", textOverflow: "ellipsis", overflow: "hidden" }}>{this.props.videoTitle}</p>
                        </div>
                    </div>
                </li>

            </div>
        );
    }
}

export default PlaylistItem;
