import React, { Component } from "react";
import { Navbar, NavbarBrand, Nav, NavItem, NavLink } from "reactstrap";
import { Link } from "react-router-dom";
import { GoogleLoginButton } from "react-social-login-buttons";
import withAuthorization from "./withAuthorization";
import SignOutButton from "./SignOut";
import * as routes from "../constants/routes";

import { auth, db } from "../firebase";

import AuthUserContext from "./AuthUserContext";
const byPropKey = (propertyName, value) => () => ({
  [propertyName]: value
});

const INITIAL_STATE = {
  error: null,
  showingAlert: false,
  showingMenu: false,
  
  users: null,
  username: "",
  photoURL: "",
  email: "",
  loading: true
};
class Navigation extends Component {
  state = { ...INITIAL_STATE };
  googleLogin = () => {
    const { history } = this.props;
    auth
      .doGoogleSignIn()
      .then(authUser => {
        localStorage.setItem("UID", authUser.user.uid)
        db.doCreateUser(
          authUser.user.uid,
          authUser.user.displayName,
          authUser.user.email,
          authUser.user.photoURL,

        )
          .then(() => {
            history.push(routes.LANDING); //redirects to Landing Page
          })
          .catch(error => {
            this.setState(byPropKey("error", error));
          });
      })
      .catch(error => {
        this.setState(byPropKey("error", error));
      });
  };


  timer = () => {
    this.setState({
      showingAlert: true
    });

    setTimeout(() => {
      this.setState({
        showingAlert: false
      });
    }, 4000);
  };

  showingMenu = () => {
    this.setState({
      showingMenu: true,
    });
    return (
      true
    )
  }



  Navigation = () => (
    <AuthUserContext.Consumer>
      {authUser =>
        authUser ? this.NavigationAuth(authUser) : this.NavigationNonAuth()
      }
    </AuthUserContext.Consumer>
  );


  NavigationAuth = ({ userInfo }) => (
    <div className="NavBar">
      <Link to={routes.LANDING}>
        <img style={{ height: "75px" }} classNameName="icon-header" src="https://i.imgur.com/aOiIHSX.png" alt="/" />
      </Link>
      <ul className="row ml-auto right-nav" >
        
        <li >
        <div className="navbar-item "><Link to={routes.MYPLAYLIST}>My Playlist</Link></div>
        </li>
        <li className="nav-item dropdown">
          <a className="navbar-item dropdown-toggle" data-toggle="dropdown" href="#" role="button" aria-haspopup="true" aria-expanded="false">
            <img ></img>
          </a>
          <div className="dropdown-menu" style={{width:"130%"}}>
            <a className="dropdown-item" href="#tab2Id">Action</a>
            <a className="dropdown-item" href="#tab3Id">Another action</a>
            <div className="dropdown-divider"></div>
            <SignOutButton />
          </div>
        </li>
      </ul>
    </div>

    // <Navbar classNameName="NavBar" expand="md">
    //   <NavbarBrand>
    // <Link to={routes.LANDING}>
    //   <img classNameName="icon-header" src="https://i.imgur.com/aOiIHSX.png" alt="/" />
    // </Link>
    //   </NavbarBrand>
    //   <Nav classNameName="ml-auto right-nav " navbar>
    //     <NavLink>
    //       <div classNameName="row">
    //         <div classNameName="navbar-item"><Link to={routes.MYPLAYLIST}>My Playlist</Link></div>
    //         <div classNameName="navbar-item"><Link to={routes.HOME}>Home Page</Link></div>
    //       </div>

    //     </NavLink>
    //     

    //   </Nav>
    // </Navbar>
  );

  NavigationNonAuth = () => (
    <Navbar classNameName="NavBar" expand="md">
      <NavbarBrand>
        <Link to={routes.LANDING}>
          <img classNameName="icon-header" src="https://i.imgur.com/aOiIHSX.png" alt="/" />
        </Link>
      </NavbarBrand>
      <Nav classNameName="ml-auto" navbar>
        {/* <NavItem>
          <NavLink>
            <Link to={routes.SIGN_IN}>Sign In</Link>
          </NavLink>
        </NavItem> */}
        <GoogleLoginButton onClick={() => this.googleLogin()} />
      </Nav>
    </Navbar>
  );

  componentDidMount() {
    const { loggedUser } = this.props;
    db.doGetAnUnser(loggedUser.uid).then(res => {
      this.setState({
        username: res.val().username,
        email: res.val().email,
        photoURL: res.val().photoURL,
        loading: false
      });
    });
  }

  render() {
    const { username, loading, email, photoURL } = this.state;
    return (

      <div>
        {
          this.Navigation()
        }
      </div>
    );
  }
}
export default Navigation;
