import React, { Component } from 'react';
import MyPlaylistItem from './MyPlaylistItem';
import { db } from "../firebase/firebase"
import { Link } from "react-router-dom";
import * as routes from "../constants/routes";


const UID = localStorage.getItem("UID");
class MyPlaylistTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            videoData : [],
            nextVideo: "",
            videoID: ""
        }
    }

    toSlug(str) {
        // Chuyển hết sang chữ thường
        str = str.toLowerCase();

        // xóa dấu
        str = str.replace(/(à|á|ạ|ả|ã|â|ầ|ấ|ậ|ẩ|ẫ|ă|ằ|ắ|ặ|ẳ|ẵ)/g, 'a');
        str = str.replace(/(è|é|ẹ|ẻ|ẽ|ê|ề|ế|ệ|ể|ễ)/g, 'e');
        str = str.replace(/(ì|í|ị|ỉ|ĩ)/g, 'i');
        str = str.replace(/(ò|ó|ọ|ỏ|õ|ô|ồ|ố|ộ|ổ|ỗ|ơ|ờ|ớ|ợ|ở|ỡ)/g, 'o');
        str = str.replace(/(ù|ú|ụ|ủ|ũ|ư|ừ|ứ|ự|ử|ữ)/g, 'u');
        str = str.replace(/(ỳ|ý|ỵ|ỷ|ỹ)/g, 'y');
        str = str.replace(/(đ)/g, 'd');

        // Xóa ký tự đặc biệt
        str = str.replace(/([^0-9a-z-\s])/g, '');

        // Xóa khoảng trắng thay bằng ký tự -
        str = str.replace(/(\s+)/g, '-');

        // xóa phần dự - ở đầu
        str = str.replace(/^-+/g, '');

        // xóa phần dư - ở cuối
        str = str.replace(/-+$/g, '');

        // return
        return str;
    }

    deletePlaylist  () {
        let db1 = db.ref(`users/${UID}/playlistList`);
        console.log(this.props.idDB);
        db1.child(this.props.idDB).remove()
    }

    UNSAFE_componentWillMount(){
        const db1 = db.ref(`users/${UID}/playlistList/${this.props.idDB}/playlistVideo`);
        let array = []
        db1.on("value", data => {
            data.forEach(element => {
                array.push({
                    key : element.key,
                    videoTitle: element.val().videoTitle,
                    videoThumbnail: element.val().videoThumbnail,
                    nextVideo: element.val().nextVideo
                })
            })
            this.setState({
                videoData : array
            });
        })
    }

    getVideo  = () => {
        if(this.state.videoData){
            return this.state.videoData.map((value, key)=>{
                return <MyPlaylistItem
                    key = {key}
                    playlistID = {this.props.idDB}
                    videoID = {value.key}
                    videoTitle = {value.videoTitle}
                    videoThumbnail = {value.videoThumbnail}
                    videoID2 = {(videoID,nextVideo)=>this.getVideoID(videoID,nextVideo)}
                />
            })
        }
    }

    getVideoID  = (videoID, nextVideo) => {
        this.setState({
            videoID: videoID,
            nextVideo: nextVideo
        });
    }

    render() {
        return (
            <div className="col-6 border"  >
                <div className="row">
                    <div className="col-9">
                        <header>{this.props.playlistTitle}</header>
                        <p>Quantity: 1/n </p>
                    </div>
                    <div className="col-3">
                        <div className="btn-group">
                            <div class="btn btn-warning" >
                            <Link to={"/editmyplaylist/"+ this.toSlug(this.props.playlistTitle) + "." + this.props.idDB + ".html"}>Edit</Link>
                                </div>
                            <button class="btn btn-danger" onClick={() => this.deletePlaylist()} >Del</button>
                        </div>
                    </div>
                </div>

                <ul className="list-group" style={{ height: "430px", overflowY: "auto", overflowX: "hidden" }}>
                    {this.getVideo()}
                </ul>




                <div>Đây là footer</div><br />
            </div>
        );
    }
}

export default MyPlaylistTable;
