import React, { Component } from 'react';
import EditMyPlaylistItem from './EditMyPlaylistItem';
import { db } from "../firebase/firebase"


const UID = localStorage.getItem("UID")
class EditMyPlaylist extends Component {
    constructor(props){
        super(props);
        this.state={
            dataFromDB : []
        }}

        UNSAFE_componentWillMount(){
            let array = [];
        const db1 = db.ref(`users/${UID}/playlistList/${this.props.match.params.id}/playlistVideo`);
        db1.on("value", data => {
            console.log(data.val());
            data.forEach(element=>{
                array.push({
                    key: element.key,
                    videoID: element.videoID,
                    videoTitle:element.videoTitle,
                    videoThumbnail: element.videoThumbnail
                })
            })

            this.setState({
                dataFromDB : array
            });
        })
        }
    
    getVideo  = () => {
        if(this.state.dataFromDB){
            return this.state.dataFromDB.map((value,key)=>{
                return <EditMyPlaylistItem
                    key = {key}
                    id = {value.key}
                    video
                />
            })
        }

    }

    render() {
        this.getData();
        return (
            <div className="container-fluid row">
                <div className="col-5">Info PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo Playlist</div>
                <div className="col-7">
                <div className="header_input mt-2 mb-3" >
                    <input  ></input>
                    <div className="fa fa-search"   ></div>
                    
                </div>
                    <EditMyPlaylistItem></EditMyPlaylistItem>
                    <EditMyPlaylistItem></EditMyPlaylistItem>
                    <EditMyPlaylistItem></EditMyPlaylistItem>
                    <EditMyPlaylistItem></EditMyPlaylistItem>
