import React, { Component } from 'react';

class VideoPlayer extends Component {
    getVideoURL  = (id) => {
        return "https://www.youtube.com/embed/" + id;
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col-">
                    <iframe width={390} height={285} src={this.getVideoURL(this.props.match.params.videoID)} frameBorder={0} allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowFullScreen />

                    </div>
                    <div className="col-5">
                    <div>
                <li className="list-group-item" style={{ display: "flex" }}>
                    <div className="row">
                        <div className="col-4">
                            <a href={this.getVideoLink(this.props.videoID)}>
                                <img src={this.props.videoThumbnail} alt="" style={{ height: "56px" }} />
                            </a>
                        </div>
                        <div className="col">
                            <p style={{ height: "30px", textOverflow: "ellipsis", overflow: "hidden" }}>{this.props.videoTitle}</p>
                        </div>
                        <button type="button" class="btn btn-danger" onClick={() => this.deleteVideo()} style={{ marginRight: "5px" }} >Del</button>
                    </div>
                </li>

            </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default VideoPlayer;
