import React, { Component } from 'react';
import { db } from "../firebase/firebase"
import MyPlaylistTable from './MyPlaylistTable';
import landdingTable from './landdingTable';

class LandingPage extends Component {
  constructor(props){super(props);
    this.state={
      dataFromDB : []
    }}
  UNSAFE_componentWillMount() {
    db.ref('playlist').on("value", data => {
      let array =[];
      data.forEach(element => {
        array.push({
          UID : element.val().UID,
          playlistID: element.val().playlistID,
        })
      });
      this.setState({
        dataFromDB: array
      });
      console.log(this.state.dataFromDB);
      // array.forEach(element => {
      //   this.setState({
          
      //   })

      // })
      
    })
  }
  getData  = () => {
    if(this.state.dataFromDB){
      this.state.dataFromDB.forEach(element=>{
        db.ref(`users/${element.UID}/playlistList/${element.playlistID}`).on("value", data => {
          return <landdingTable playlistTitle={data.val().playlistTitle}/>
        })
      })
      
    }
    
    // if(this.state.dataFromDB){
    //   return this.state.dataFromDB.map((value, key) => {
    //     return <MyPlaylistTable
    //       key ={key}
    //     />
    //   } )
    // }
  }
  render() {
    return (
      <div className="container-fluid">
        {this.getData}
      </div>
    );
  }
}

export default LandingPage;
