import React, { Component } from 'react';
import {db} from "../firebase/firebase"

const UID = localStorage.getItem("UID")

class VideoPlayer extends Component {
    constructor(props){
        super(props);
        this.state={
            item : []
        }}
    getVideoURL = (id) => {
        return "https://www.youtube.com/embed/" + id;
    }

    UNSAFE_componentWillMount(){
        const db1 = db.ref(`users/${UID}/playlistList/${this.props.match.params.playlistID}/playlistVideo`)
        db1.on("value", data => {
            let array= [];
            data.forEach(element => {
                array.push({
                    videoID : element.val().videoID,
                    videoTitle: element.val().videoTitle,
                    videoThumbnail: element.val().videoThumbnail
                })
            })
            this.setState();
        })
    }


    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col-7">
                        <iframe width={390} height={285} src={this.getVideoURL(this.props.match.params.videoID)} frameBorder={0} allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowFullScreen />

                    </div>
                    <div className="col-5">
                        <ul className="list-group" style={{ height: "430px", overflowY: "auto", overflowX: "hidden" }}>
                            <li className="list-group-item">
                                <div className="row">
                                    <div className="col-4">
                                        <div>
                                            {/* <Link to={"/videoplayer/"+ this.props.playlistID + "." + this.props.videoID}> */}
                                            <img src="https://i.ytimg.com/vi/7hoq-xoAOSc/hqdefault.jpg" alt="" style={{ height: "56px" }} />
                                            {/* </Link> */}
                                        </div>
                                    </div>
                                    <div className="col-8">
                                        <p style={{ width: "200px", height: "30px", textOverflow: "ellipsis", overflow: "hidden" }}>loremloremloremloremloremlorem</p>
                                    </div>
                                </div>
                            </li>
                        </ul>
                    </div>
                </div>
            </div>
        );
    }
}

export default VideoPlayer;
