import React, { Component } from "react";

import withAuthorization from "./withAuthorization";
import { db } from "../firebase";

class HomePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      idVideo: '',
      thumbnailVideo: '',
      titleVideo: '',
      users: null,
      username: "",
      photoURL: "",
      email: "",
      loading: true
    }
  }

  getThumbnail = (id) => {
    let a = "https://i.ytimg.com/vi/";
    let b = "/hqdefault.jpg";
    this.setState({
      thumbnailVideo: a + id + b
    });
  }

  getTitle = (id) => {
    let a = 'https://www.googleapis.com/youtube/v3/videos?key=AIzaSyCAaefSzfa_SLpbO1Mt7hl8faCwfwBgk_E&fields=items(snippet(title))&part=snippet,statistics&id=' + id;
    this.getJSON(a, function (err, data) {
      if (err === null && data !== null) {
        // console.log(data.items[0].snippet.title);
        this.setState({
          titleVideo: data.items[0].snippet.title
        });
      }
      else return err;
    })
  }

  getJSON = function (url, callback) {
    var xhr = new XMLHttpRequest();
    xhr.open('GET', url, true);
    xhr.responseType = 'json';
    xhr.onload = function () {
      var status = xhr.status;
      if (status === 200) {
        callback(null, xhr.response);
      } else {
        callback(status, xhr.response);
      }
    };
    xhr.send();
  };

  getItem = (idVideo) => {
    this.getThumbnail(idVideo);
    this.getTitle(idVideo);
  }

  componentDidMount() {
    const { loggedUser } = this.props;
    db.doGetAnUnser(loggedUser.uid).then(res => {
      this.setState({
        username: res.val().username,
        email: res.val().email,
        photoURL: res.val().photoURL,
        loading: false
      });
    });
  }

  render() {
    const { username, loading, email, photoURL } = this.state;
    return (
      <div>
        <h1>Home</h1>
        {!loading &&
          <div className="centered">
            <h3>Hello {username}!</h3>
            <img src={photoURL} alt="/" style={{ height: "100px", borderRadius: "50%" }} />
            <p>{email}</p>
          </div>}
        <div>
          <SearchBar getItem={(item) => { this.getItem(item) }} />
          <SearchResult thumbnailVideo={this.state.thumbnailVideo} titleVideo={this.state.titleVideo} />
        </div>
      </div>
    );
  }
}

const authCondition = authUser => !!authUser;

export default withAuthorization(authCondition)(HomePage);
