import React, { Component } from "react";
import { Navbar, NavbarBrand, Nav, NavItem, NavLink } from "reactstrap";
import { Link } from "react-router-dom";
import { GoogleLoginButton } from "react-social-login-buttons";

import SignOutButton from "./SignOut";
import * as routes from "../constants/routes";

import { auth, db } from "../firebase";

import AuthUserContext from "./AuthUserContext";
const byPropKey = (propertyName, value) => () => ({
  [propertyName]: value
});

const INITIAL_STATE = {
  error: null,
  showingAlert: false,
  showingMenu: false,
  userName: "",
  userEmail: "",
  userPhoto: "",
};
class Navigation extends Component {
  state = { ...INITIAL_STATE };
  googleLogin = () => {
    const { history } = this.props;
    auth
      .doGoogleSignIn()
      .then(authUser => {
        localStorage.setItem("UID", authUser.user.uid)
        db.doCreateUser(
          authUser.user.uid,
          authUser.user.displayName,
          authUser.user.email,
          authUser.user.photoURL,

        )
          .then(() => {
            history.push(routes.HOME); //redirects to Home Page
          })
          .catch(error => {
            this.setState(byPropKey("error", error));
          });
      })
      .catch(error => {
        this.setState(byPropKey("error", error));
      });
  };


  timer = () => {
    this.setState({
      showingAlert: true
    });

    setTimeout(() => {
      this.setState({
        showingAlert: false
      });
    }, 4000);
  };

  showingMenu = () => {
    this.setState({
      showingMenu: true,
    });
    return (
      true
    )
  }



  Navigation = () => (
    <AuthUserContext.Consumer>
      {authUser =>
        authUser ? this.NavigationAuth(authUser) : this.NavigationNonAuth()
      }
    </AuthUserContext.Consumer>
  );


  NavigationAuth = ({ userInfo }) => (
    <div className="NavBar">
      <Link to={routes.LANDING}>
        <img style={{ height: "75px" }} classNameName="icon-header" src="https://i.imgur.com/aOiIHSX.png" alt="/" />
      </Link>
      <ul className="row ml-auto right-nav" >

        <li >
          <div className="navbar-item "><Link to={routes.MYPLAYLIST}>My Playlist</Link></div>
        </li>
        <li className="nav-item dropdown">
          <a className="navbar-item dropdown-toggle" data-toggle="dropdown" href="#" role="button" aria-haspopup="true" aria-expanded="false">Account</a>
          <div className="dropdown-menu" style={{ width: "130%" }}>
            <div className="row">
              <div className="col-5">
                <img src={this.state.userPhoto} style={{ height: "100px", borderRadius: "50%" }}></img>
              </div>
              <div className="col-7">
                <p>{this.props.userName}</p>
                <p>{this.props.userEmail}</p>
              </div>
            </div>
            <div className="dropdown-divider"></div>
            <SignOutButton />
          </div>
        </li>
      </ul>
    </div>
  );

  NavigationNonAuth = () => (
    <div className="NavBar">
      <Link to={routes.LANDING}>
        <img style={{ height: "75px" }} classNameName="icon-header" src="https://i.imgur.com/aOiIHSX.png" alt="/" />
      </Link>
      <ul className="row ml-auto right-nav" >
        <GoogleLoginButton onClick={() => this.googleLogin()} />
      </ul>
    </div>
  );
  render() {
    return (
      <div>
        {
          this.Navigation()
        }
      </div>
    );
  }
}
export default Navigation;
