import React, { Component } from 'react';
import YouTube from 'react-youtube';
import { Redirect, Link } from 'react-router-dom';

class VideoStream extends Component {
    constructor(props) {
        super(props);
        this.state = {
            videoID: "",
        }
    }


    videoOnReady = (event) => {
        event.target.playVideo();
    }
    // videoStateChange = (event) => {
    //     if (event.data === 0) {
    //         this.func(this.props.nextVideo)
    //     }
    // }
    videoStateChange = (event) => {
        if (event.data === 0) {
            console.log("abc");
            return (<Redirect to={"/videoplayer" + this.props.playlistID + "." + this.props.nextVideo}/>)
        }
    }

    // videoStateChange = (event) => {
    //     if (event.data === 0) {
    //         window.location.replace(`/videoplayer/${this.props.playlistID}.${this.props.nextVideo}`)
    //     }
    // }

    directlink  = () => {
        console.log("abc");
        return (<Link to="/"></Link>);
    }

    func  = (param) => {
        this.props.func(param)
    }

    render() {
        const opts = {
            height: '390',
            width: '640',
            playerVars: {
                autoplay: 1,
                vq: "small",
            },
        };
        return (
            <div>
                <YouTube
                    videoId={this.props.videoID}
                    opts={opts}
                    onReady={this.videoStateChange}
                    onStateChange={this.videoStateChange}
                />
                <button onClick={() => {this.directlink()}}>Click</button>
            </div>
        );
    }
}

export default VideoStream;
