import React, { Component } from 'react';
import MyPlaylistTable from './MyPlaylistTable';
import SearchBar from "./SearchBar";
import SearchResult from "./SearchResult";
import { db } from "../firebase/firebase"

const UID = localStorage.getItem("UID");


class MyPlaylist extends Component {
    constructor(props) {
        super(props);
        this.state = {
            playlistTitle: "",
            playlistDes: "",
            playlistAM: "",
            videoID: "",
            thumbnailVideo: '',
            titleVideo: '',
            item: {},
            dataFromDB: []
        }
    }

    isChange(event) {
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]: value
        });
    }

    getThumbnail = (id) => {
        let a = "https://i.ytimg.com/vi/";
        let b = "/hqdefault.jpg";
        this.setState({
            thumbnailVideo: a + id + b
        });
    }

    getTitle = (id) => {
        let a = 'https://www.googleapis.com/youtube/v3/videos?key=AIzaSyCAaefSzfa_SLpbO1Mt7hl8faCwfwBgk_E&fields=items(snippet(title))&part=snippet,statistics&id=' + id;
        this.getJSON(a, (err, data) => {
            if (err === null && data !== null) {
                this.setState({
                    titleVideo: data.items[0].snippet.title
                });
            }
            else return err;
        })
    }

    getJSON = function (url, callback) {
        var xhr = new XMLHttpRequest();
        xhr.open('GET', url, true);
        xhr.responseType = 'json';
        xhr.onload = function () {
            var status = xhr.status;
            if (status === 200) {
                callback(null, xhr.response);
            } else {
                callback(status, xhr.response);
            }
        };
        xhr.send();
    };

    getItem = (idVideo) => {
        this.setState({
            titleVideo: "",
            thumbnailVideo: ""
        });
        this.getThumbnail(idVideo);
        this.getTitle(idVideo);
        this.setState({
            videoID: idVideo
        });
    }

    createPlaylist = (item) => {
        item.playlistDes = this.state.playlistDes;
        item.playlistTitle = this.state.playlistTitle;
        item.firstVideo = this.state.videoID;
        item.lastVideo = this.state.videoID;
        item.quantity = 1;
        item.UID = UID
        let accessModifier = (this.state.playlistAM == 0) ? "private" : "public";
        if (this.state.playlistAM && this.state.playlistDes && this.state.playlistTitle && this.state.videoID) {
            const db1 = db.ref(`playlist/${accessModifier}`)
            let str1 = db1.push(item).getKey();
            const db2 = db.ref(`playlist/${accessModifier}/${str1}/playlistVideo/${this.state.videoID}`);
            db2.set({
                videoThumbnail: this.state.thumbnailVideo,
                videoTitle: this.state.titleVideo,
                nextVideo: this.state.videoID,
                previousVideo: this.state.videoID,
            })
            const db3 = db.ref(`users/${UID}/playlistList`);
            db3.push({
                playlistID: str1,
                playlistAM: this.state.playlistAM,
            })
            // alert("Thêm mới thành công")
        } else alert("Vui lòng điền toàn bộ thông tin");
    }

    deletePlaylist = (playlistIDofUser, accessModifier, idDB) => {
        db.ref(`users/${UID}/playlistList/`).child(playlistIDofUser).remove();
        db.ref(`playlist/${accessModifier}/`).child(idDB).remove();
        console.log("abc");
    }

    componentDidMount() {
        const db1 = db.ref(`users/${UID}/playlistList`)
        db1.on("value", data1 => {
            if (data1.val()) {
                let array = [];
                data1.forEach(element => {
                    const accessModifier = (element.val().playlistAM == 0) ? "private" : "public";
                    const db2 = db.ref(`playlist/${accessModifier}/${element.val().playlistID}`);
                    db2.on("value", data2 => {
                        if (data2.val()) {
                            array.push({
                                playlistIDofUser: element.key,
                                key: data2.key,
                                playlistTitle: data2.val().playlistTitle,
                                accessModifier: accessModifier,
                                quantity: data2.val().quantity,
                            })
                            this.setState({
                                dataFromDB: [...array]
                            });
                        }
                    })

                })
            }
        })
    }

    getData = () => {
        if (this.state.dataFromDB) {
            return this.state.dataFromDB.map((value, key) => {
                return (
                    <MyPlaylistTable
                        playlistIDofUser={value.playlistIDofUser}
                        accessModifier={value.accessModifier}
                        key={key}
                        idDB={value.key}
                        playlistTitle={value.playlistTitle}
                        deletePlaylist={(playlistIDofUser, accessModifier, idDB) => this.deletePlaylist(playlistIDofUser, accessModifier, idDB)}
                        quantity={value.quantity}
                    ></MyPlaylistTable>
                )
            })
        }
    }
    render() {
        return (

            <div className="container-fluid">
                <div className="row">
                    <div className="col-8">
                        <div className="row">
                            {this.getData()}
                        </div>

                    </div>

                </div>
                <div className="button-add-playlist dropup" >
                    <div className="dropdown">
                        <button className="navbar-item dropdown-toggle fa fa-plus" type="button" id="dropdownMenuButton" style={{ color: "#007bff", backgroundColor: "transparent", border: "none" }} data-toggle="dropdown" >
                            <img alt="" src="https://i.imgur.com/aOiIHSX.png" style={{ height: "50px", borderRadius: "50%" }} />
                        </button>
                        <div className="dropdown-menu" style={{ width: "350px", left: "-255px" }}>
                            <form className="border text-center create-playlist-table">
                                <h3>Tạo Playlist</h3>
                                <div className="form-group ">
                                    <label>Tên Playlist</label>
                                    <input type="text" name="playlistTitle" onChange={(event) => this.isChange(event)} className="form-control mb-4" placeholder="Tên Playlist ... " required />
                                    <label>Mô tả Playlist</label>
                                    <input type="text" name="playlistDes" onChange={(event) => this.isChange(event)} className="form-control mb-4" placeholder="Mô tả Playlist ... " required />
                                    <div className="form-group">
                                        <label>Phân quyền</label>
                                        <select name="playlistAM" className="form-control mb-4" onChange={(event) => this.isChange(event)} defaultValue="Lựa chọn phân quyền">
                                            <option>Lựa chọn phân quyền</option>
                                            <option value={1}>Công khai</option>
                                            <option value={0}>Bí mật</option>
                                        </select>
                                    </div>
                                    <label>Link video </label>
                                    <SearchBar getItem={(item) => { this.getItem(item) }} />
                                    <SearchResult thumbnailVideo={this.state.thumbnailVideo} titleVideo={this.state.titleVideo} />
                                    <div className="dropdown-divider"></div>
                                    <input type="button" onClick={(item) => this.createPlaylist(this.state.item)} className="btn btn-primary btn-lg btn-block " value="Thêm Playlist" />
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default MyPlaylist;
