import React, { Component } from 'react';
import {Link} from "react-router-dom";

class PlaylistItem extends Component {
    constructor(props){
        super(props);
        this.state={
            videoInfo : {}
        }}

    sendVideoInfo  = (videoID, nextVideo, playlistID) => {
        this.props.videoID2 (videoID, nextVideo, playlistID )
    }

    render() {
        return (
            <div>
                <li className="list-group-item">
                    <div className="row">
                        <div className="col-4">
                            <div>
                                <Link to={"/videoplayer/"+ this.props.playlistID + "." + this.props.videoID}>
                                <img onClick={(videoID, videoInfo, playlistID) => this.sendVideoInfo(this.props.videoID, this.props.nextVideo, this.props.playlistID)} src={this.props.videoThumbnail} alt="" style={{ height: "56px" }} />
                                </Link>
                            </div>
                        </div>
                        <div className="col-8">
                            <p style={{ width: "200px", height: "30px", textOverflow: "ellipsis", overflow: "hidden" }}>{this.props.videoTitle}</p>
                        </div>
                    </div>
                </li>

            </div>
        );
    }
}

export default PlaylistItem;
