import Redux from 'react-redux';

class App extends Component {

  render() {
    var redux = require('redux')
    let oldState = {
      array:[1,3,2],
      editStatus: true
    }
    let reducer1  = (state = oldState, action) => {
      switch (action.type){
        case "CHANGE_EDIT_STATUS": return {...state,editStatus: !state.editStatus} 
        case "ADD_NEW": return{...state,array:[...state.array,action.newItem]}
        case "DELETE": return{...state,array:[...state.array.filter((value,key)=> key != action.input)]}
      }return state;
    }
    let store1 = redux.createStore(reducer1);
    console.log(store1.getState());
    store1.dispatch({type:"CHANGE_EDIT_STATUS"})
    console.log(store1.getState());
    store1.dispatch({type:"ADD_NEW", newItem:5})
    console.log(store1.getState());
    store1.dispatch({type:"DELETE", input:0})
    console.log(store1.getState());
    return (
      <div className="container-fluid">


      </div>
    )

  }
}

export default App;
