import React, { Component } from 'react';
import EditMyPlaylistItem from './EditMyPlaylistItem';
import { db } from "../firebase/firebase"
import SearchBar from "./SearchBar";
import SearchResult from "./SearchResult";


const UID = localStorage.getItem("UID")
class EditMyPlaylist extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dataFromDB: [],
            videoID: "",
            thumbnailVideo: '',
            titleVideo: '',
            item: {},
            firstVideo: "",
            lastVideo: ""
        }
    }

    UNSAFE_componentWillMount() {
        const db2 = db.ref(`users/${UID}/playlistList/${this.props.match.params.id}`);
        db2.on("value", data => {
            if (data.val()) {
                const accessModifier = (data.val().playlistAM == 0) ? "private" : "public";
                const db1 = db.ref(`playlist/${accessModifier}/${data.val().playlistID}`);
                db1.on("value", data1 => {
                    this.setState({
                        firstVideo: data1.val().firstVideo,
                        lastVideo: data1.val().lastVideo,

                    });
                })
                const db3 = db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo`);
                db3.on("value", data => {
                    let array = [];
                    data.forEach(element => {
                        array.push({
                            key: element.key,
                            videoID: element.val().videoID,
                            videoTitle: element.val().videoTitle,
                            videoThumbnail: element.val().videoThumbnail,

                        })
                    })
                    this.setState({
                        dataFromDB: [...array]
                    });
                })
            }
        })
    }

    deleteVideo = (videoID) => {
        console.log(db.ref(`playlist/public/-MGwZg0KkX2SSobQWJJ1/playlistVideo/${videoID}/nextVideo`).on("value",data));
        // if (videoID == this.state.firstVideo) {
        //     const db2 = db.ref(`users/${UID}/playlistList/${this.props.match.params.id}`);
        //     db2.on("value", data => {
        //         if (data.val()) {
        //             const accessModifier = (data.val().playlistAM == 0) ? "private" : "public";
        //             db.ref(`playlist/${accessModifier}/${data.val().playlistID}/firstVideo`).set(db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${videoID}/nextVideo`).on());
        //         }
        //     });
        // }
        // else if (videoID == this.state.lastVideo)
        // else
    }

    getVideo = () => {
        if (this.state.dataFromDB) {
            return this.state.dataFromDB.map((value, key) => {
                return (<EditMyPlaylistItem
                    deleteVideo={(videoID) => this.deleteVideo(videoID)}
                    key={key}
                    playlistID={this.props.match.params.id}
                    videoID={value.key}
                    videoTitle={value.videoTitle}
                    UID={UID}
                    videoThumbnail={value.videoThumbnail}
                />)
            })
        }

    }

    getThumbnail = (id) => {
        let a = "https://i.ytimg.com/vi/";
        let b = "/hqdefault.jpg";
        this.setState({
            thumbnailVideo: a + id + b
        });
    }

    getTitle = (id) => {
        let a = 'https://www.googleapis.com/youtube/v3/videos?key=AIzaSyCAaefSzfa_SLpbO1Mt7hl8faCwfwBgk_E&fields=items(snippet(title))&part=snippet,statistics&id=' + id;
        this.getJSON(a, (err, data) => {
            if (err === null && data !== null) {
                this.setState({
                    titleVideo: data.items[0].snippet.title
                });
            }
            else return err;
        })
    }

    getJSON = function (url, callback) {
        var xhr = new XMLHttpRequest();
        xhr.open('GET', url, true);
        xhr.responseType = 'json';
        xhr.onload = function () {
            var status = xhr.status;
            if (status === 200) {
                callback(null, xhr.response);
            } else {
                callback(status, xhr.response);
            }
        };
        xhr.send();
    };

    getItem = (idVideo) => {
        this.setState({
            titleVideo: "",
            thumbnailVideo: ""
        });
        this.getThumbnail(idVideo);
        this.getTitle(idVideo);
        this.setState({
            videoID: idVideo
        });
    }

    addVideo = (item) => {
        const db1 = db.ref(`users/${UID}/playlistList/${this.props.match.params.id}`);
        db1.on("value", data => {
            const accessModifier = (data.val().playlistAM == 0) ? "private" : "public";
            const db2 = db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${this.state.videoID}`);
            db2.set({
                videoThumbnail: this.state.thumbnailVideo,
                videoTitle: this.state.titleVideo,
                nextVideo: this.state.firstVideo,
                previousVideo: this.state.lastVideo,
            })
            const db3 = db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${this.state.firstVideo}/previousVideo`);
            db3.set(this.state.videoID)
            const db4 = db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${this.state.lastVideo}/nextVideo`);
            db4.set(this.state.videoID)
            const db5 = db.ref(`playlist/${accessModifier}/${data.val().playlistID}/lastVideo`);
            db5.set(this.state.videoID)
        })
    }

    render() {
        return (
            <div className="container-fluid row edit-my-playlist">
                <div className="col-5">
                    <p>
                        Info PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo PlaylistInfo Playlist
                    </p>
                </div>
                <form className="col-7">
                    <div className="mt-2 mb-3" >
                        <SearchBar getItem={(item) => { this.getItem(item) }} />
                        <SearchResult thumbnailVideo={this.state.thumbnailVideo} titleVideo={this.state.titleVideo} />

                    </div>
                    <button type="button" className="btn btn-info" onClick={(item) => { this.addVideo(this.state.item) }}>Add to Playlist</button>
                    {this.getVideo()}

                </form>
            </div>
        );
    }
}

export default EditMyPlaylist;
