import React, { Component } from 'react';
import PlaylistItem from './MyPlaylistItem';

class LandingTable extends Component {
    constructor(props){
        super(props);
        this.state={
            playlistVideo : []
        }}
    getVideo  = () => {
        const array = []
        if(this.props.playlistVideo){
            this.props.playlistVideo.forEach(element => {
                 array.push({
                nextVideo: this.props.playlistVideo.nextVideo,
                videoThumnail: this.props.playlistVideo.videoThumnail,
                videoTitle: this.props.playlistVideo.videoTitle
            })
            })
           
        }
        this.setState({
            playlistVideo: array
        });
    }

    render() {
        console.log(this.state.playlistVideo);
        return (
            <div className="col-4 border">
                <div className="row">
                    <div className="col-9">
                        <header>{this.props.playlistTitle}</header>
                        <p>Quantity: 1/n </p>
                    </div>
                </div>
                <ul className="list-group" style={{ height: "430px", overflowY: "auto", overflowX: "hidden" }}>
                    {this.getVideo()}
                </ul>




                <div>Đây là footer</div><br />
            </div>
        );
    }
}

export default LandingTable;
