import React, {Component} from "react";
import { BrowserRouter, Route } from "react-router-dom";
import { Container } from "reactstrap";

import * as routes from "../constants/routes";

//nav stuff
import Navigation from "./Navigation";
import LandingPage from "./Landing";
import HomePage from "./Home";

import withAuthentication from "./withAuthentication";



class App extends Component {
  constructor(props){
    super(props);
    this.state={
      idVideo: ''
    }
  }
  youtube_parser(url) {
    var regExp = /^.*((youtu.be\/)|(v\/)|(\/u\/\w\/)|(embed\/)|(watch\?))\??v?=?([^#&?]*).*/;
    var match = url.match(regExp);
    return (match && match[7].length == 11) ? match[7] : false;
  }

  isChange  = (event) => {
    // const name = event.target.name;
    // const value = event.target.value;
    // this.setState({
    //   [name]:value
    // });
    this.setState({
      idVideo: this.youtube_parser(event.target.value)
    });
  }
  
  getThumbnail  = (id) => {
    let a = "https://img.youtube.com/vi/";
    let b = "/hqdefault.jpg";
    return a + id + b;
  }

  render() {
    return (
      <div className="col-4">
        <input onChange={ (event) => this.isChange(event)} type="text" className="form-control" aria-describedby="helpId" placeholder="" />
        <button type="button" class="btn btn-primary">Tim kiem</button>
        <br /> <br />
        <div class="card text-left">
          <img class="card-img-top" src="holder.js/100px180/" alt="" />
          <div class="card-body">
    <h4 class="card-title">Head</h4>
            {/* <img src={this.getThumbnail(this.state.idVideo)} alt="/" /> */}
            {console.log(this.getThumbnail(this.state.idVideo))}
          </div>
        </div>
      </div>
    );
  }
}

  
  
