import React, { Component } from "react";
import { Navbar, NavbarBrand, Nav, NavItem, NavLink } from "reactstrap";
import { Link } from "react-router-dom";
import { GoogleLoginButton } from "react-social-login-buttons";

import SignOutButton from "./SignOut";
import * as routes from "../constants/routes";

import { auth, db } from "../firebase";

import AuthUserContext from "./AuthUserContext";
const byPropKey = (propertyName, value) => () => ({
  [propertyName]: value
});

const INITIAL_STATE = {
  email: "",
  password: "",
  error: null,
  showingAlert: false
};
class Navigation extends Component {
  state = { ...INITIAL_STATE };
  googleLogin = () => {
    const { history } = this.props;
    auth
      .doGoogleSignIn()
      .then(authUser => {
        localStorage.setItem("UID", authUser.user.uid)
        db.doCreateUser(
          authUser.user.uid,
          authUser.user.displayName,
          authUser.user.email,
          authUser.user.photoURL,

        )
          .then(() => {
            history.push(routes.HOME); //redirects to Home Page
          })
          .catch(error => {
            this.setState(byPropKey("error", error));
          });
      })
      .catch(error => {
        this.setState(byPropKey("error", error));
      });
  };


  timer = () => {
    this.setState({
      showingAlert: true
    });

    setTimeout(() => {
      this.setState({
        showingAlert: false
      });
    }, 4000);
  };

  Navigation = () => (
    <AuthUserContext.Consumer>
      {authUser =>
        authUser ? this.NavigationAuth(authUser) : this.NavigationNonAuth()
      }
    </AuthUserContext.Consumer>
  );

  NavigationAuth = ({ userInfo }) => (
    <Navbar className="NavBar"  expand="md">
      <NavbarBrand>
        <Link to={routes.LANDING}>
          <img className = "icon-header" src="https://i.imgur.com/aOiIHSX.png" alt="/"/>
        </Link>
      </NavbarBrand>
      <Nav className="ml-auto right-nav collapse navbar-collapse" navbar>
        <NavItem>
          <NavLink>
            <Link to={routes.MYPLAYLIST}>My Playlist</Link>
            <Link to={routes.HOME}>Home Page</Link>
            <Link to={routes.VIDEOPLAYER}>PLAYER</Link>

          </NavLink>
        </NavItem>
        <NavItem>
          <SignOutButton />
        </NavItem>
      </Nav>
    </Navbar>
  );

  NavigationNonAuth = () => (
    <Navbar className="NavBar"  expand="md">
      <NavbarBrand>
        <Link to={routes.LANDING}>
          <img className = "icon-header" src="https://i.imgur.com/aOiIHSX.png" alt="/"/>
        </Link>
      </NavbarBrand>
      <Nav className="ml-auto" navbar>
        {/* <NavItem>
          <NavLink>
            <Link to={routes.SIGN_IN}>Sign In</Link>
          </NavLink>
        </NavItem> */}
        <GoogleLoginButton onClick={() => this.googleLogin()} />
      </Nav>
    </Navbar>
  );
  render() {
    return (
      <div>
        {
          this.Navigation()
        }
      </div>
    );
  }
}
export default Navigation;
