import React, { Component } from 'react';
import { db } from "../firebase/firebase"


class EditMyPlaylistItem extends Component {

    getVideoLink(videoID) {
        return "https://www.youtube.com/watch?v=" + videoID
    }

    deleteVideo = (videoID) => {
        this.props.deleteVideo(videoID);
    }

    render() {
        return (
            <div>
                <li className="edit-myplaylist-item">
                    <div className="row" style={{width:"100%"}}>
                        <div className="col-3">
                            <a href={this.getVideoLink(this.props.videoID)}>
                                <img src={this.props.videoThumbnail} alt="" style={{ height: "99px" }} />
                            </a>
                        </div>
                        <div className="col-7">
                            <p style={{ height: "30px", fontSize:"20px",textOverflow: "ellipsis", overflow: "hidden" }}>{this.props.videoTitle}</p>
                        </div>
                        <div className="col">
                            <button className="btn btn-danger" type="button" onClick={() => this.deleteVideo()} style={{width:"100%", height:"100%", fontSize:"20px" }} >Del</button>
                        </div>

                    </div>
                </li>

            </div>
        );
    }
}

export default EditMyPlaylistItem;
