import React, { Component } from 'react';
import { db } from "../firebase/firebase"
import PlaylistItem from './MyPlaylistItem';
import VideoStream from './VideoStream';

class VideoPlayer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dataFromDB: [],
            nextVideo: "",
            videoID: ""
        }
    }

    UNSAFE_componentWillMount() {
        const {playlistID} = this.props.match.params;
        const accessModifier = (db.ref(`playlist/public`).on("value", data => data.val().playlistID)) ? "public" : "private";
        db.ref(`playlist/public`).on("value", data => console.log(data.val().playlistID))
        const db2 = db.ref(`playlist/${accessModifier}/${this.props.match.params.playlistID}/playlistVideo/${this.props.match.params.videoID}/nextVideo`)
        db2.on("value", data => this.setState({
            videoID: this.props.match.params.videoID,
            nextVideo: data.val()
        }))
        const db1 = db.ref(`playlist/${accessModifier}/${this.props.match.params.playlistID}/playlistVideo`)
        db1.on("value", data => {
            let array = [];
            data.forEach(element => {
                array.push({
                    videoID: element.key,
                    videoTitle: element.val().videoTitle,
                    videoThumbnail: element.val().videoThumbnail,
                    nextVideo: element.val().nextVideo,
                })
            })
            this.setState({
                dataFromDB: array
            });
        })
        //  if (db.ref(`playlist/private/${this.props.match.params.playlistID}`).on("value", data => data.val())){
        //     const db2 = db.ref(`playlist/private/${this.props.match.params.playlistID}/playlistVideo/${this.props.match.params.videoID}/nextVideo`)
        //     db2.on("value", data => this.setState({
        //         videoID: this.props.match.params.videoID,
        //         nextVideo: data.val()
        //     }))
        //     const db1 = db.ref(`playlist/private/${this.props.match.params.playlistID}/playlistVideo`)
        //     db1.on("value", data => {
        //         let array = [];
        //         data.forEach(element => {
        //             array.push({
        //                 videoID: element.key,
        //                 videoTitle: element.val().videoTitle,
        //                 videoThumbnail: element.val().videoThumbnail,
        //                 nextVideo: element.val().nextVideo,
        //             })
        //         })
        //         this.setState({
        //             dataFromDB: array
        //         });
        //     })
        // }

    }

    getVideo = () => {
        if (this.state.dataFromDB) {
            return this.state.dataFromDB.map((value, key) => {
                return <PlaylistItem

                    key={key}
                    videoID={value.videoID}
                    videoThumbnail={value.videoThumbnail}
                    videoTitle={value.videoTitle}
                    playlistID={this.props.match.params.playlistID}
                    nextVideo={value.nextVideo}
                    videoID2={(videoID, nextVideo) => this.getVideoID(videoID, nextVideo)}
                />
            })
        }
    }

    getVideoID = (videoID, nextVideo) => {
        this.setState({
            videoID: videoID,
            nextVideo: nextVideo
        });
    }

    func = (params) => {
        this.setState({
            videoID: params
        });
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col-7">
                        <VideoStream videoID={this.state.videoID} nextVideo={this.state.nextVideo} func={(params) => this.func(params)}></VideoStream>
                    </div>
                    <div className="col-5">
                        <ul className="list-group" style={{ height: "430px", overflowY: "auto", overflowX: "hidden" }}>
                            {this.getVideo()}
                        </ul>
                    </div>
                </div>
            </div>
        );
    }
}

export default VideoPlayer;
