            // </div>
            <div className="dropdown">
                <button className="navbar-item dropdown-toggle"
                    type="button"
                    id="dropdownMenuButton" data-toggle="dropdown">
                    Account</button>
                <div className="dropdown-menu" style={{ width: "350px", left: "-255px" }}>
                    <form >
                        <button type="button" className="btn btn-primary editUserInfo-button">Edit Info</button>
                    </form>
                    <div className="dropdown-divider"></div>
                    <a className="dropdown-item" href="#">New around here? Sign up</a>
                    <a className="dropdown-item" href="#">Forgot password?</a>
                </div>
            </div>
        );
    }
}

