import React, { Component } from 'react';
import MyPlaylistItem from './MyPlaylistItem';
import { db } from "../firebase/firebase";
import { Link } from "react-router-dom"


class LandingTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            videoData: [],
            nextVideo: "",
            videoID: "",
            videoThumbnail: "",
            videoTitle: "",
        }
    }

    deletePlaylist() {
        let db1 = db.ref(`users/${this.props.UID}/playlistList`);
        console.log(this.props.idDB);
        db1.child(this.props.idDB).remove()
    }

    componentDidMount() {
        // const db1 = db.ref(`playlist/public/${this.props.idDB}/playlistVideo`);
        // let array = []
        // db1.on("value", data => {
        //     if (data.val()) {
        //         data.forEach(element => {
        //             array.push({
        //                 key: element.key,
        //                 videoTitle: element.val().videoTitle,
        //                 videoThumbnail: element.val().videoThumbnail
        //             })
        //         })
        //         this.setState({
        //             videoData: array
        //         });
        //     }
        // })

        db.ref(`playlist/public/${this.props.idDB}/firstVideo`).once("value", data => {
            db.ref(`playlist/public/${this.props.idDB}/playlistVideo/${data.val()}`).once("value", data1 => {
                if (data1.val()) {
                    this.setState({
                        videoID: data1.key,
                        videoThumbnail: data1.val().videoThumbnail,
                        videoTitle: data1.val().videoTitle,
                    });
                }
            })
        })
    }

    // getVideo = () => {
    //     if (this.state.videoData) {
    //         return this.state.videoData.map((value, key) => {
    //             return <MyPlaylistItem
    //                 key={key}
    //                 playlistID={this.props.idDB}
    //                 videoID={value.key}
    //                 videoTitle={value.videoTitle}
    //                 videoThumbnail={value.videoThumbnail}
    //                 videoID2={(videoID, nextVideo) => this.getVideoID(videoID, nextVideo)}
    //             />
    //         })
    //     }
    // }

    getVideoID = (videoID, nextVideo) => {
        this.setState({
            videoID: videoID,
            nextVideo: nextVideo
        });
    }

    render() {
        return (
                <div className="col-3 "  >
                    <div className="playlist-table">

                        <Link to={"/videoplayer/" + this.props.idDB + "." + this.state.videoID}>
                            <div style={{position:"relative", left:"0", top:"0"}}>
                                <img src={this.state.videoThumbnail} alt="" style={{ width: "100%", position:"relative", top:"0", left:"0" }} />
                                <div style={{height:"100%", width:"33%", top:"0", right:"0", backgroundColor:"#000000cc",position:"absolute"}}>
                                    <p style={{ color: "white", fontSize: "20px" , margin: "50% 0"}}>{this.props.quantity} <br/>video</p>
                                </div>
                            </div>
                            
                            <p style={{ color: "white", fontSize: "20px", width: "100%", height: "55px", textOverflow: "ellipsis", overflow: "hidden" }}>Playlist - {this.props.playlistTitle} - {this.state.videoTitle} </p>
                        </Link>
                    </div>

                </div>
        );
    }
}

export default LandingTable;
