import React, { useState, useEffect } from 'react';
import EditMyPlaylistItem from './EditMyPlaylistItem';
import { db } from "../firebase/firebase"
import SearchBar from "./SearchBar";
import SearchResult from "./SearchResult";


const UID = localStorage.getItem("UID")
function EditMyPlaylist(props) {
    const [dataFromDB, setDataFromDB] = useState([]);
    const [videoID, setVideoID] = useState("");
    const [thumbnailVideo, setThumbnailVideo] = useState("");
    const [item, setItem] = useState({});
    const [firstVideo, setFirstVideo] = useState("");
    const [lastVideo, setLastVideo] = useState("");
    const [playlistDes, setPlaylistDes] = useState("");
    const [playlistTitle, setPlaylistTitle] = useState("");
    const [playlistID, setPlaylistID] = useState("");
    const [quantity, setQuantity] = useState("");

    useEffect(() => {
        const db2 = db.ref(`users/${UID}/playlistList/${this.props.match.params.id}`);
        db2.on("value", data => {
            if (data.val()) {
                const accessModifier = (data.val().playlistAM == 0) ? "private" : "public";
                const db1 = db.ref(`playlist/${accessModifier}/${data.val().playlistID}`);
                db1.on("value", data1 => {
                    if (data1.val()) {
                        setFirstVideo(data1.val().firstVideo);
                            setLastVideo(data1.val().lastVideo);
                            setPlaylistID(data.val().playlistID);
                            setQuantity(data.val().quantity);
                            setPlaylistDes(data1.val().playlistDes);
                            setPlaylistTitle(data1.val().playlistTitle);
                        }
                })
                const db3 = db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo`);
                db3.on("value", data => {
                    let array = [];
                    data.forEach(element => {
                        array.push({
                            key: element.key,
                            videoID: element.val().videoID,
                            videoTitle: element.val().videoTitle,
                            videoThumbnail: element.val().videoThumbnail,

                        })
                    })
                    setDataFromDB([...array])
                })
            }
        })
    }, [])

    const getVideoID = (videoID, videoTitle) => {
        this.setState({
            videoID: videoID,
            videoTitle: videoTitle
        });
    }

    const deleteVideo = (videoID) => {
        db.ref(`users/${UID}/playlistList/${this.props.match.params.id}`).on("value", data => {
            if (data.val()) {
                let accessModifier = (data.val().playlistAM == 0) ? "private" : "public";
                if (this.state.quantity == 1) alert("Vui lòng xóa playlist")
                else {
                    if (videoID == this.state.firstVideo) {
                        console.log("first video");
                        db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${videoID}`).on("value", data2 => {
                            if (data2.val()) {
                                db.ref(`playlist/${accessModifier}/${data.val().playlistID}/firstVideo`).set(data2.val().nextVideo);
                                db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${data2.val().nextVideo}/previousVideo`).set(data2.val().previousVideo);
                                db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${data2.val().previousVideo}/nextVideo`).set(data2.val().nextVideo);
                                this.setState({ firstVideo: data2.val().nextVideo });
                            }
                        })

                    }
                    else if (videoID == this.state.lastVideo) {
                        console.log("last video");
                        db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${videoID}`).on("value", data2 => {
                            if (data2.val()) {
                                db.ref(`playlist/${accessModifier}/${data.val().playlistID}/lastVideo`).set(data2.val().previousVideo);
                                db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${data2.val().nextVideo}/previousVideo`).set(data2.val().previousVideo);
                                db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${data2.val().previousVideo}/nextVideo`).set(data2.val().nextVideo);
                                this.setState({ lastVideo: data2.val().previousVideo });
                            }
                        })
                    }
                    else {
                        console.log("normal video");
                        db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${videoID}`).on("value", data2 => {
                            if (data2.val()) {
                                db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${data2.val().nextVideo}/previousVideo`).set(data2.val().previousVideo);
                                db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${data2.val().previousVideo}/nextVideo`).set(data2.val().nextVideo);
                            }
                        })
                    }
                    db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo`).child(videoID).remove();
                    db.ref(`playlist/${accessModifier}/${data.val().playlistID}/quantity`).set(this.state.quantity -= 1)

                }
            }
        });
    }

    const getVideo = () => {
        if (this.state.dataFromDB) {
            return this.state.dataFromDB.map((value, key) => {
                return (<EditMyPlaylistItem
                    deleteVideo={(videoID) => this.deleteVideo(videoID)}
                    key={key}
                    playlistID={this.state.playlistID}
                    videoID={value.key}
                    videoTitle={value.videoTitle}
                    videoThumbnail={value.videoThumbnail}
                    videoID2={(videoID, videoTitle) => this.getVideoID(videoID, videoTitle)}
                />)
            })
        }

    }

    const getThumbnail = (id) => {
        let a = "https://i.ytimg.com/vi/";
        let b = "/hqdefault.jpg";
        this.setState({
            thumbnailVideo: a + id + b
        });
    }

    const getTitle = (id) => {
        let a = 'https://www.googleapis.com/youtube/v3/videos?key=AIzaSyCAaefSzfa_SLpbO1Mt7hl8faCwfwBgk_E&fields=items(snippet(title))&part=snippet,statistics&id=' + id;
        this.getJSON(a, (err, data) => {
            if (err === null && data !== null) {
                this.setState({
                    titleVideo: data.items[0].snippet.title
                });
            }
            else return err;
        })
    }

    const getJSON = function (url, callback) {
        var xhr = new XMLHttpRequest();
        xhr.open('GET', url, true);
        xhr.responseType = 'json';
        xhr.onload = function () {
            var status = xhr.status;
            if (status === 200) {
                callback(null, xhr.response);
            } else {
                callback(status, xhr.response);
            }
        };
        xhr.send();
    };

    const getItem = (idVideo) => {
        this.setState({
            titleVideo: "",
            thumbnailVideo: ""
        });
        this.getThumbnail(idVideo);
        this.getTitle(idVideo);
        this.setState({
            videoID: idVideo
        });
    }

    const addVideo = (item) => {
        const db1 = db.ref(`users/${UID}/playlistList/${this.props.match.params.id}`);
        db1.on("value", data => {
            if (data.val()) {
                const accessModifier = (data.val().playlistAM == 0) ? "private" : "public";
                const db2 = db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${this.state.videoID}`);
                db2.once("value", data1 => {
                    if (!data1.val()) {
                        db2.set({
                            videoThumbnail: this.state.thumbnailVideo,
                            videoTitle: this.state.titleVideo,
                            nextVideo: this.state.firstVideo,
                            previousVideo: this.state.lastVideo,
                        })
                        db.ref(`playlist/${accessModifier}/${data.val().playlistID}/quantity`).set(this.state.quantity += 1);
                        this.setState({ lastVideo: this.state.videoID });
                    } else alert("Video đã có trong playlist")
                })
                db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${this.state.firstVideo}/previousVideo`).set(this.state.videoID);
                db.ref(`playlist/${accessModifier}/${data.val().playlistID}/playlistVideo/${this.state.lastVideo}/nextVideo`).set(this.state.videoID);
                db.ref(`playlist/${accessModifier}/${data.val().playlistID}/lastVideo`).set(this.state.videoID);
            }
        })
    }

    return (
        <div className="container-fluid row edit-my-playlist">
            <div className="col-5">
                <h1 className="playlist-info" style={{ color: "white" }}> {this.state.playlistTitle}</h1>
                <h1 className="playlist-info" style={{ color: "white" }}>{this.state.playlistDes}</h1>
                <h1 className="playlist-info" style={{ color: "white" }}>{this.state.quantity} video</h1>
            </div>
            <form className="col-7">
                <div className="mt-2 mb-3" >
                    <SearchBar getItem={(item) => { this.getItem(item) }} />
                    <div style={{ color: "white", fontSize: "20px" }}>
                        <SearchResult thumbnailVideo={this.state.thumbnailVideo} titleVideo={this.state.titleVideo} />

                    </div>

                </div>
                <button type="button" className="btn btn-info" onClick={(item) => { this.addVideo(this.state.item) }}>Add to Playlist</button>
                {this.getVideo()}

            </form>
        </div>
    );
}

export default EditMyPlaylist;
