import React, { Component } from "react";

import withAuthorization from "./withAuthorization";
import { db } from "../firebase";

class HomePage extends Component {
  state = {
    users: null,
    username: "",
    loading: true
  };

  componentDidMount() {
    const { loggedUser } = this.props;
    db.doGetAnUnser(loggedUser.uid).then(res => {
      console.log(this.props.loggedUser.photoURL);
      this.setState({
        username: res.val().username,
        loading: false
      });
    });
  }

  render() {
    const { username, loading } = this.state;
    return (
      <div>
        <h1>Home</h1>
        {!loading && <div className="centered">Hello {username}!<img src={this.props.loggedUser.photoURL} alt="/" style={{ height: "100px", borderRadius: "50%" }} /></div>}

        <p style={{ marginTop: "80px" }}>
          NOTE: This page is only accessible by signed in users.
        </p>
      </div>
    );
  }
}

const authCondition = authUser => !!authUser;

export default withAuthorization(authCondition)(HomePage);
