import React, { Component } from 'react';
import { db } from "../firebase/firebase"
import PlaylistItem from './MyPlaylistItem';
import YouTube from 'react-youtube';
import {Link} from "react-router-dom"

const UID = localStorage.getItem("UID")

class VideoPlayer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            videoID : "",
            dataFromDB: [],
            nextVideo: ""
        }
    }

    UNSAFE_componentWillReceiveProps(){
        const db1 = db.ref(`users/${UID}/playlistList/${this.props.match.params.playlistID}/playlistVideo/${this.props.match.params.videoID}/nextVideo`);
        db1.on("value", data => {
            console.log(data.val());
            this.setState({
            videoID : this.props.match.params.videoID,
            nextVideo: data.val()
        });
        })

        
    }

    UNSAFE_componentWillMount() {
        this.setState({
            videoId : this.props.match.params.videoID
        });
        const db1 = db.ref(`users/${UID}/playlistList/${this.props.match.params.playlistID}/playlistVideo`)
        db1.on("value", data => {
            let array = [];
            data.forEach(element => {
                array.push({
                    videoID: element.key,
                    videoTitle: element.val().videoTitle,
                    videoThumbnail: element.val().videoThumbnail,
                    nextVideo: element.val().nextVideo,
                })
            })
            this.setState({
                dataFromDB: array
            });
        })
    }

    getVideo = () => {
        if (this.state.dataFromDB) {
            return this.state.dataFromDB.map((value, key) => {
                return <PlaylistItem
                    key={key}
                    videoID={value.videoID}
                    videoThumbnail={value.videoThumbnail}
                    videoTitle={value.videoTitle}
                    playlistID={this.props.match.params.playlistID}
                    nextVideo={value.nextVideo}
                />
            })
        }
    }

    videoOnReady = (event) => {
        // access to player in all event handlers via event.target
        event.target.pauseVideo();
    }
    videoStateChange = (event) => {
        if (event.data === 0) {
            return <Link to={"/videoplayer/"+ this.props.playlistID + "." + this.state.nextVideo} />
        }
    }

    render() {
        const opts = {
            height: '390',
            width: '640',
            playerVars: {
                autoplay: 1,
            },
        };
        return (
            <div className="container">
                <div className="row">
                    <div className="col-7">
                        <YouTube
                            videoId={this.state.videoID}
                            opts={opts}
                            onReady={this.videoOnReady}
                            onStateChange={this.videoStateChange}
                        />
                    </div>
                    <div className="col-5">
                        <ul className="list-group" style={{ height: "430px", overflowY: "auto", overflowX: "hidden" }}>
                            {this.getVideo()}
                        </ul>
                    </div>
                </div>
            </div>
        );
    }
}

export default VideoPlayer;
