import React, { Component } from 'react';
import SignOutButton from "./SignOut";

class UserInfoMenu extends Component {
    constructor(props) {
        super(props);
        this.state = {
            buttonState: true,
        }
    }
    editUserInfo = () => {
        if (this.state.buttonState) {
            return (<div type="button" onClick={() => this.editClick()} className="btn btn-primary editUserInfo-button">Edit Info</div>)
        } else return <div type="button"  onClick={() => this.saveClick()} className="btn btn-success editUserInfo-button">Save</div>
    }

    usernameState  = () => {
        if(this.state.buttonState) return (<p>{this.props.userName}</p>)
        else return (<input defaultValue={this.props.userName}></input>)

    }

    editClick = () => {
        this.setState({
            buttonState: false
        });
    }
    saveClick = () => {
        this.setState({
            buttonState: true
        });
    }

    render() {
        return (
            // <div>
            //     <a className="navbar-item dropdown-toggle" data-toggle="dropdown" href="#" role="button" aria-haspopup="true" aria-expanded="false">Account</a>
            //     <div className="dropdown-menu" style={{ width: "350px", left: "-255px" }}>
            //         <div className="userInfo row">
            //             <div className="col-4">
            //                 <img src={this.props.userPhoto} alt="/" style={{ height: "100px", borderRadius: "50%" }}></img>
            //             </div>
            //             <div className="col-8">
            //                 <p>{this.props.userName}</p>
            //                 <p>{this.props.userEmail}</p>
            //                 {this.editUserInfo()}
            //             </div>
            //         </div>
            //         <div className="dropdown-divider"></div>
            //         <SignOutButton />
            //     </div>
            // </div>
            <div className="dropdown">
                <a className="navbar-item dropdown-toggle" type="button" id="dropdownMenuButton" style={{color: "#007bff"}} data-toggle="dropdown" >Account</a>
                <div className="dropdown-menu" style={{ width: "350px", left: "-255px" }}>
                    <form >
                    <div className="userInfo row">
                        <div className="col-4">
                            <img src={this.props.userPhoto} alt="/" style={{ height: "100px", borderRadius: "50%" }}></img>
                        </div>
                        <div className="col-8">
                            {this.usernameState}
                            <p>{this.props.userEmail}</p>
                            {this.editUserInfo()}
                        </div>
                    </div>
                    </form>
                    <div className="dropdown-divider"></div>
                    <SignOutButton />
                </div>
            </div>
        );
    }
}

export default UserInfoMenu;
